sysauto_script.script:
var autoCloseOverdue =gs.getProperty('x_58872_needit.autoCloseOverdue');
var overdueTasks= new GlideRecord('x_58872_needit_needit_task');
overdueTasks.addQuery('state','<',3);
overdueTasks.addQuery('due_date','<',gs.daysAgo(autoCloseOverdue));
overdueTasks.query();
while(overdueTasks.next()){
	overdueTasks.setValue('state',4);
	gs.info("Auto close NeedIt Task record = " + overdueTasks.number);
	overdueTasks.work_notes = "Record closed because it was too far past the Due date.";
	overdueTasks.update();
}
